import React, {Component} from "react";
import {MDBBox} from "mdbreact";





class BoxPage extends Component{
    constructor(props){
        super(props)
        this.state ={
          name: "",
        }

        this.handleChange = this.handleChange.bind(this);

    }
    displayWelcome=(event) =>{
        this.setState((props,state) =>({
            content:  this.state.name
        })
    )
}

handleChange(event){
    this.setState({name:event.target.value})
}

    render(){
        return(
            <MDBBox className="customBox">
             <input type="text"  placeholder="Enter your name "  className="personalinput" />


                    <MDBBox className="welcomeText"> 
                       {this.state.content + this.state.name}
                    </MDBBox>




             </MDBBox>
        );
    }
}




export default  BoxPage;
