import React, {Component} from "react";
import {MDBBox,MDBAnimation} from "mdbreact";
import InputPage from "./inputbox";




class BoxPage extends Component{
    constructor(props){
        super(props)
        this.state ={
          content: "Hello",
          newcontent:"Welcome"
        }

    }
    displayWelcome=(props) =>{
        this.setState((props,state) =>({
            content: this.state.newcontent + this.props.name
        })
    )
}

    render(){
        return(
            <MDBBox className="customBox">
                 <InputPage  onFocus={this.displayWelcome}/> 
                 <MDBAnimation type="" count={3} duration="600ms" delay="2s">
                 <MDBBox className="welcomeText">  {this.state.content} </MDBBox>

</MDBAnimation>


             </MDBBox>
        );
    }
}




export default  BoxPage;
