import React, {Component} from "react";
import {MDBBox,MDBBtn} from "mdbreact";





class BoxPage extends Component{
    constructor(props){
        super(props)
        this.state ={
          name: "",
          message:""
        }

        this.handleChange = this.handleChange.bind(this);

    } 

handleChange(event){
    this.setState({name:event.target.value, message: "WELCOME"})
}
handleClick=(event)=>{

    var x =document.querySelectorAll("inputblock")
    if (x.style.display === "block"){
        x.style.display = "none"
    } else {
        x.style.display = "block"
    }

}

    render(){
        return(
            <MDBBox className="customBox">
                <MDBBox className="welcomeText"> 
                      {this.state.message + " " + this.state.name}
                    </MDBBox>
             <input type="text" id="inputblock"  placeholder="Enter your name " style={{color:"white"}} onChange={this.handleChange}  className="personalinput" /> 
             <MDBBtn gradient="purple" id="" size="sm" onClick={this.handleClick}>
                 Get Started</MDBBtn>

             </MDBBox>
        );
    }
}




export default  BoxPage;
