import React, {Component} from "react";
import {MDBBox} from "mdbreact";





class BoxPage extends Component{
    constructor(props){
        super(props)
        this.state ={
          name: "",
          message:""
        }

        this.handleChange = this.handleChange.bind(this);

    }





handleChange(event){
    this.setState({name:event.target.value, message: "Welcome"})
}

    render(){
        return(
            <MDBBox className="customBox">
             <input type="text"  placeholder="Enter your name " onChange={this.handleChange}  className="personalinput" />


                    <MDBBox className="welcomeText"> 
                      {this.state.message + " " + this.state.name}
                    </MDBBox>




             </MDBBox>
        );
    }
}




export default  BoxPage;
