import React, {Component} from "react";
import {MDBBox} from "mdbreact";
import InputPage from "./inputbox";




class BoxPage extends Component{
    constructor(props){
        super(props)
        this.state ={

         content:"  " ,
          name: "Lewis",
        }

        this.handleChange = this.handleChange.bind(this);

    }
    displayWelcome=(event) =>{
        this.setState((props,state) =>({
            content:  this.state.name
        })
    )
}

handleChange(event){
    this.setState({name:event.target.value})
}

    render(){
        return(
            <MDBBox className="customBox">
                 <InputPage  onFocus={this.displayWelcome} onChange={this.handleChange}/> 

                    <MDBBox className="welcomeText"> 
                       {this.state.content + this.state.name}
                    </MDBBox>




             </MDBBox>
        );
    }
}




export default  BoxPage;
