import React, {Component} from "react";
import {MDBBox,MDBAnimation} from "mdbreact";
import InputPage from "./inputbox";




class BoxPage extends Component{
    constructor(props){
        super(props)
        this.state ={
          content: "",
          newcontent:"Welcome"
        }
        this.state = {
            name: ""
        };
        this.handleChange = this.handleChange.bind(this);

    }
    displayWelcome=(props) =>{
        this.setState((props,state) =>({
            content: this.state.newcontent + this.state.name
        })
    )
}

handleChange(event){
    this.setState({name:event.target.value})
}

    render(){
        return(
            <MDBBox className="customBox">
                 <InputPage  onFocus={this.displayWelcome} onChange={this.handleChange}/> 

                    <MDBBox className="welcomeText"> 
                        <MDBAnimation type="" count={3} duration="600ms" delay="2s"> 

                        {this.state.content} 
                        </MDBAnimation>
                    </MDBBox>




             </MDBBox>
        );
    }
}




export default  BoxPage;
