import React, {Component} from "react";
import {MDBBox} from "mdbreact";
import InputPage from "./inputbox";




class BoxPage extends Component{
    constructor(props){
        super(props)
        this.state ={
          content: "Hello",
          newcontent:"Welcome"
        }

    }
    displayWelcome=(props) =>{
        this.setState((props,state) =>({
            content: this.state.newcontent + this.props.name
        })
    )
}

    render(){
        return(
            <MDBBox className="customBox">
                 <InputPage  onFocus={this.displayWelcome}/> <br />
             {this.state.content}
             </MDBBox>
        );
    }
}




export default  BoxPage;
